import vertex from '../shaders/vertex.glsl'
import fragment from '../shaders/fragment.glsl'

// Ensure ThreeJS is in global scope for the 'examples/'
global.THREE = require("three");

// Include any additional ThreeJS examples below
require("three/examples/js/controls/OrbitControls");

const canvasSketch = require("canvas-sketch");

const settings = {
  // Make the loop animated
  animate: true,
  // Get a WebGL canvas rather than 2D
  context: "webgl"
};

const sketch = ({ context }) => {
  // Create a renderer
  const renderer = new THREE.WebGLRenderer({
    canvas: context.canvas
  });

  //attach the canvas to the dom
  const container = context.canvas
  container.appendChild(renderer.domElement)

  // WebGL background color
  renderer.setClearColor(0xeeeeee, 1);
  renderer.physicallyCorrectLights = true;
  renderer.outputEncoding = THREE.sRGBEncoding;

  // Setup a camera
  const camera = new THREE.PerspectiveCamera(70, window.innerwidth/window.innerHeight, 0.001, 1000);
  camera.position.set(0, 0, -4);
  camera.lookAt(new THREE.Vector3());

  // Setup camera controller
  const controls = new THREE.OrbitControls(camera, context.canvas);

  // Setup your scene
  const scene = new THREE.Scene();

  // Setup a geometry
  let material = new THREE.ShaderMaterial({
    extensions: {
      derivatives: '#extension GL_OES_standard_derivatives : enable'
    },
    side:  THREE.DoubleSide,
    uniforms:{
      time: { type: "f", value: 0 },
      resolution: { type: "v4", value: new THREE.Vector4() },
      uvRate1: {
        value: new THREE.Vector2(1, 1)
      }
    },
    vertexShader: vertex,
    fragmentShader: fragment
  })

  let geometry = new THREE.PlaneGeometry(1, 1, 1, 1);

  let plane = new THREE.Mesh(geometry, material);
  scene.add(plane);

  // draw each frame
  return {
    // Handle resize events here
    resize({ pixelRatio, viewportWidth, viewportHeight }) {
      renderer.setPixelRatio(pixelRatio);
      renderer.setSize(viewportWidth, viewportHeight);
      camera.aspect = viewportWidth / viewportHeight;
      
      material.uniforms.resolution.value.x = viewportWidth;
      material.uniforms.resolution.value.y = viewportHeight;
      material.uniforms.resolution.value.z = viewportWidth/viewportHeight;
      material.uniforms.resolution.value.w = viewportHeight/viewportWidth;
      
      camera.updateProjectionMatrix();
    },
    // Setup a material
    // addObjects() {
    //   let material = new THREE.ShaderMaterial({
    //     extensions: {
    //       derivatives: '#extension GL_OES_standard_derivatives : enable'
    //     },
    //     side:  THREE.DoubleSide,
    //     uniforms:{
    //       time: { type: "f", value: 0 },
    //       resolution: { type: "v4", value: new THREE.Vector4() },
    //       uvRate1: {
    //         value: new THREE.Vector2(1, 1)
    //       }
    //     },
    //     vertexShader: vertex,
    //     fragmentShader: fragment
    //   })

    //   let geometry = new THREE.PlaneGeometry(1, 1, 1, 1);

    //   let plane = new THREE.Mesh(geometry, material);
    //   scene.add(plane);
    // },
    // Update & render your scene here
    render({ time }) {
      controls.update();
      renderer.render(scene, camera);
    },
    // Dispose of events & renderer for cleaner hot-reloading
    unload() {
      controls.dispose();
      renderer.dispose();
    }
  };
};

canvasSketch(sketch, settings);
